const truncateContent = (content) => {
    const maxWords = 70;
    const words = content.split(' ');

    if (words.length <= maxWords) {
        return content;
    }

    const truncatedWords = words.slice(0, maxWords);
    return `${truncatedWords.join(' ')}...`;
};

